import './App.css';
import React, { useEffect, useState } from "react";
import Select from 'react-select'
//import Weather from './Components/Weather';



const options = [
  { value: 'India', label: 'India' },
  { value: 'UK', label: 'UK' },
  { value: 'vanilla', label: 'Vanilla' }
]

const options2 = [
  { country:'India', value: 'Chennai', label: 'Chennai' },
  { country:'India', value: 'Bangalore', label: 'Bangalore' },
  { country:'UK', value: 'London', label: 'London' }
]


export default function App() {

  const [lat, setLat] = useState([]);
  const [long, setLong] = useState([]);
  const [data, setData] = useState([]);

  const fetchData = async () => {
    navigator.geolocation.getCurrentPosition(function(position) {
      setLat(position.coords.latitude);
      setLong(position.coords.longitude);
    });

    await fetch(`${process.env.REACT_APP_API_URL}/weather/?lat=${lat}&lon=${long}&units=metric&APPID=dfbbc9612ad3e9bbc36199bcfacbefe4`)
    .then(res => res.json())
    .then(result => {
      setData(result)
      console.log(result);
    });
  }

  useEffect(() => {
    fetchData();
  }, [lat,long])

  return (
    <div className="App">

      {(typeof data.main != 'undefined') ? (
        <Weather weatherData={data}/>
      ): (
        <div></div>
      )}

    </div>
  );
}

function OnSelectionChange (selectedOption) {
  const [stateOption, setStateOption] = useState([]);
  setStateOption("Jana")
  console.log(selectedOption)
}




const WeatherDetails = (props)=>{
  const {weatherData} = props
  return(<div>
  <h1>SS Solutions</h1>
  <Select options={options} onChange={OnSelectionChange} />
  <Select options={stateOption} onChange={OnSelectionChange} />
</div>)
}

function Weather(weatherData) {


  if(weatherData.weatherData.cod===200) { 

    return(
      <WeatherDetails weatherData={weatherData}  />
    )
  }
  else{
  return (<h1>Error 404</h1>)
  }

}
